var appPerfil=new Vue({el:"#formPerfil",created:function(){this.getValues()},data:{user:JSON.parse(localStorage.getItem("user")),options:{headers:{Authorization:localStorage.getItem("token")}},api:"../api/actualizarUsuario"},methods:{update:function(){this.$http.put(this.api,this.user,this.options).then(function(t){t.data&&localStorage.setItem("user",JSON.stringify(t.data.update))},function(t){var e=t.body.message;switch(t.body.code){case 0:case 1:alert(e);break;case 3:this.user.password="",alert(e);break;default:console.log(t.body)}}).catch(function(t){console.log("Caught",t)})},getValues:function(){$("#pais").addClass("flag-icon-"+this.user.countryCode)}}});